mapper.select:
		select * from movie
		
			
			
				name like "%"#{name}"%"
			
			
			
				and actor like "%"#{actor}"%"
			
			
			
				and year=#{year}
			
			
			
				and uptime >= #{startDate}
			

			
				and uptime <= #{endDate}
			

			
			
				and price >= #{startPrice}
			

			
				and price <= #{endPrice}
			

           
			
				and longtime >= #{startLongtime}
			

			
				and longtime <= #{endLongtime}
			
		
		
		
			order by ${orderField} ${orderMethod}
